INSERT INTO `sx_special` VALUES ('18', '0', '0', '1', '创会成员：卢灿林', '', '和十八道顺德私房菜馆的老板卢灿林吃饭是一件相当惬意的事情，一进门他就把我们带到了收银台旁的专属茶座。一张古朴的桌子，加一', '', '', '', '0', '2007', 'http://06.8660.org/file/upload/201603/22/2011221026.jpg', '', '0', '0', '0', '', '', '', '卢灿林', '1458648895', '袁金', '1458655999', '', '0', '3', '0', '', '', '', '', '1', '0', '10.00', '000011', '32', '2', '0', '0', '1000');
